mapper.select:
SELECT a.meeting_id AS meetingId, b.nickname AS leaderNickname, c.place_name AS placeName, c.address AS placeAddress, d.sport_name AS sportName, a.status_id AS statusId, a.meeting_title AS meetingTitle, a.date, a.start_time AS startTime, a.end_time AS endTime, a.min_user AS minUser, a.max_user AS maxUser, a.gender, a.age_group AS ageGroup, a.level, a.custom_place_address AS customPlaceAddress FROM meeting a JOIN member b ON a.leader_id = b.member_id LEFT JOIN place c ON a.place_id = c.place_id JOIN sport_type d ON a.sport_id = d.sport_id WHERE 1=1
AND a.gender = #{gender}
AND (
FIND_IN_SET(#{item}, a.age_group)
)
AND (
FIND_IN_SET(#{item}, a.level)
)
AND a.sport_id IN
#{item}
AND a.status_id IN
#{item}
AND a.date >= #{minDate}
AND a.date <= #{maxDate}
ORDER BY CASE WHEN a.status_id IN (1, 2) AND a.date = CURDATE() AND a.start_time >= CURTIME() THEN 0 ELSE 1 END, a.date ASC, a.start_time ASC, CASE WHEN a.status_id IN (1, 2) THEN a.status_id ELSE 3 END ASC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM meeting a
JOIN member b ON a.leader_id = b.member_id
LEFT JOIN place c ON a.place_id = c.place_id
JOIN sport_type d ON a.sport_id = d.sport_id
WHERE 1=1


    AND a.gender = #{gender}



    AND (

        FIND_IN_SET(#{item}, a.age_group)

    )



