mapper.select:
SELECT ph.penalty_id, ph.member_id, u.user_name AS member_name, ph.penalty_type, ph.reason, ph.created_at, ph.is_active, ph.post_id, ph.comment_id, ph.review_id FROM user_penalty_history ph JOIN user u ON ph.member_id = u.user_id WHERE 1 = 1
AND ph.penalty_type = #{req.penaltyType}
AND ph.member_id = #{req.memberId}
ORDER BY ph.created_at DESC LIMIT #{req.size} OFFSET #{req.offset}

SELECT COUNT(*)
FROM user_penalty_history ph
WHERE 1 = 1

    AND ph.penalty_type = #{req.penaltyType}


    AND ph.member_id = #{req.memberId}

SELECT COUNT(*)
FROM user_penalty_history
WHERE member_id = #{memberId}
AND penalty_type = #{penaltyType}
